insert into wz_book (book_serial_number,book_isbn,book_name_cn,book_name_english,book_name_foreign,book_series_cn,book_language,book_bilingual,book_content_intr_cn,book_content_intr_foreign,book_author,book_author_foreign,book_editor,book_translator,book_translator_foreign,book_copyright_word_count,book_neiwen_page_count,book_paper_price,book_weight,book_keyword_cn,book_keyword_english,book_keyword_foreign,book_publish_time,book_publish_count,book_print_count,book_paper_cover_style_file,book_paper_publish_pdf,book_e_pdf,book_paper_neiwen_style_file,book_publish_soft,book_paper_word,book_design_cover_company,book_e_mobi,book_e_epub,book_e_xml,book_neiwen_serverpath,book_cover_serverpath,book_font_serverpath,book_pdf_publish_serverpath,book_word_serverpath,book_xml_serverpath,book_epub_serverpath,book_mobi_serverpath,book_pdf_read_serverpath,book_html_serverpath,book_contract_serverpath, user_id) values ('EP_9787508522937_003_c2jpe','9787508522937','黄色行李','','无','文学系列','003--中文','','玛尔塔·罗哈斯的《黄色行李》讲述的是：十九世纪中后期，古巴仍然是欧洲的一块殖民地。工业发展和国内市场的仆拓部在呼吁一支廉价的劳动力大军。正凶为如此，从1842年开始，往古巴贩运中国苦力的罪恶交易就开始了。作者以十九世纪下半叶充满矛盾冲突的时期为背景，描写故事的两位主人公范倪与尼古拉斯关系的过程巾，突显了两种文化的差异，以及因文化差异而导致的双方误解和歧义，经过细致的布局安排和前期对东方文化的确凿调研，从历史和民族融合的角度艺术地勾勒出一个虚幻的世界，在这个世界里，丰富的人物和场景相互交融，汇成今天多元文化特色鲜明的古巴岛。小说的题目《黄色行李》具有双重所指：一是黄皮肤的来自中国的苦力；二是文中提及的来自中国的商品。尼古拉斯机会主义的敏锐和他无孔不入的嗅觉使他成为贩卖苦力的最重要的组织者，不断往秘鲁钦查和古巴岛上输送中国劳工。这项肮脏交易的遮羞布下充满了各种丑恶和冒险：贿赂、虚伪、欺骗、残酷、贩运途中经过大洋时的万般惊险。','无','玛尔塔·罗哈斯','无','郑磊','张鹏','无',0,200,28,0,'','','无','2012-6',1,1,'有','有','无','有','','无','张红','有','无','无','\\201409之前书目\\EP_9787508522937_003_c2jpe\\排版','\\201409之前书目\\EP_9787508522937_003_c2jpe\\封面','\\201409之前书目\\EP_9787508522937_003_c2jpe\\字体','\\201409之前书目\\EP_9787508522937_003_c2jpe\\印刷PDF','\\201409之前书目\\EP_9787508522937_003_c2jpe\\WORD','\\201409之前书目\\EP_9787508522937_003_c2jpe\\XML','\\201409之前书目\\EP_9787508522937_003_c2jpe\\EPUB','\\201409之前书目\\EP_9787508522937_003_c2jpe\\MOBI','\\201409之前书目\\EP_9787508522937_003_c2jpe\\阅读PDF','\\201409之前书目\\EP_9787508522937_003_c2jpe\\H