insert into wz_book (book_serial_number,book_isbn,book_name_cn,book_name_english,book_name_foreign,book_series_cn,book_language,book_bilingual,book_content_intr_cn,book_content_intr_foreign,book_author,book_author_foreign,book_editor,book_translator,book_translator_foreign,book_copyright_word_count,book_neiwen_page_count,book_paper_price,book_weight,book_keyword_cn,book_keyword_english,book_keyword_foreign,book_publish_time,book_publish_count,book_print_count,book_paper_cover_style_file,book_paper_publish_pdf,book_e_pdf,book_paper_neiwen_style_file,book_publish_soft,book_paper_word,book_design_cover_company,book_e_mobi,book_e_epub,book_e_xml,book_neiwen_serverpath,book_cover_serverpath,book_font_serverpath,book_pdf_publish_serverpath,book_word_serverpath,book_xml_serverpath,book_epub_serverpath,book_mobi_serverpath,book_pdf_read_serverpath,book_html_serverpath,book_contract_serverpath, user_id) values ('EP_7508505980_001_im8mP','7508505980','中国道教','Daoism in China','无','中国宗教基本情况系列','001--英文','','介绍中国的宗教，不能不首先谈及道教，因为只有道教承袭了中国原始社会最古老的信仰传统。中国是一个多民族多宗教长期并存的国家，到了近代主要是佛教、道教、伊斯兰教、天主教和基督教五大宗教并存，其中只有道教是产生于中国本土的宗教。道教所宣扬的宇宙生成观，同中国传统的宇宙生成观是一致的；道教所主张的崇尚自然、清净寡欲乖为人处事的准则，成为数千年来中国人重要的审美追求；道教为人们描绘的神仙世界和流传的神仙故事，在中国大多数群众中世代相传，可以说是达到了家喻户晓，许多道教的信仰内容已经演变成了中国普通老百姓习以为常的民间习俗。因此，即使是一些外来宗教传到中国后，也难免受到道教的影响。由五洲传播出版社出版、王宜峨教授撰写的《中国道教》一书，客观、真实地介绍了中国道教的思想渊源、形成和历史发展、新中国建立后道教界的活动，以及道教的神仙信仰、宫观制度、经籍编撰和在中国社会及世界的广泛影响。','无','王宜峨','无','荆孝敏','曾传辉','无',0,226,48,0,'','Daoism in China','无','2004-10',1,1,'有','有','无','无','PageMaker 只有内文','无','北京紫航文化艺术有限公司','有','无','无','\\201409之前书目\\EP_7508505980_001_im8mP\\排版','\\201409之前书目\\EP_7508505980_001_im8mP\\封面','\\201409之前书目\\EP_7508505980_001_im8mP\\字体','\\201409之前书目\\EP_7508505980_001_im8mP\\印刷PDF','\\201409之前书目\\EP_7508505980_001_im8mP\\WORD','\\201409之前书目\\EP_7508505980_001_im8mP\\XML','\\201409之前书目\\EP_7508505980_001_im8mP\\EPUB','\\201409之前书目\\EP_7508505980_001_im8mP\\MOBI','\\201409之前书目\\EP_7508505980_001_im8mP\\阅读PDF','\\201409之前书目\\EP_7508505980_001_im8mP\\HTML