--
-- Table structure for table `wz_book`
--
DROP TABLE IF EXISTS `wz_book`;
CREATE TABLE `wz_book` (
  `book_id` int(0) unsigned zerofill NOT NULL AUTO_INCREMENT,
  `book_serial_number` varchar(30) DEFAULT NULL COMMENT '图书编号',
  `book_name_cn` varchar(200) DEFAULT NULL COMMENT '中文书名',
  `book_name_english` varchar(200) DEFAULT NULL COMMENT '英文书名',
  `book_name_foreign` varchar(200) DEFAULT NULL COMMENT '外文书名',
  `book_name_fa` varchar(200) DEFAULT NULL COMMENT '法文书名',
  `book_name_xi` varchar(200) DEFAULT NULL COMMENT '西文书名',
  `book_name_e` varchar(200) DEFAULT NULL COMMENT '阿文书名',
  `book_author` varchar(50) DEFAULT NULL COMMENT '著者',
  `book_author_english` varchar(100) DEFAULT NULL COMMENT '著者英文名',
  `book_author_foreign` varchar(100) DEFAULT NULL COMMENT '著者外文名',
  `book_author_fa` varchar(100) DEFAULT NULL COMMENT '著者法文名',
  `book_author_xi` varchar(100) DEFAULT NULL COMMENT '著者西文名',
  `book_author_e` varchar(100) DEFAULT NULL COMMENT '著者阿文名',
  `book_editor` varchar(50) DEFAULT NULL COMMENT '责编',
  -- `book_editor_english` varchar(100) DEFAULT NULL COMMENT '编责英文名',
  -- `book_editor_foreign` varchar(100) DEFAULT NULL COMMENT '编责外文名',
  -- `book_editor_fa` varchar(100) DEFAULT NULL COMMENT '责编法文名',
  -- `book_editor_xi` varchar(100) DEFAULT NULL COMMENT '编责西文名',
  -- `book_editor_e` varchar(100) DEFAULT NULL COMMENT '编责阿文名',
  `book_translator` varchar(50) DEFAULT NULL COMMENT '译者',
  `book_translator_english` varchar(100) DEFAULT NULL COMMENT '译者英文名',
  `book_translator_foreign` varchar(100) DEFAULT NULL COMMENT '译者外文名',
  `book_translator_fa` varchar(100) DEFAULT NULL COMMENT '译者法文名',
  `book_translator_xi` varchar(100) DEFAULT NULL COMMENT '译者西文名',
  `book_translator_e` varchar(100) DEFAULT NULL COMMENT '译者阿文名',
  `book_isbn` varchar(20) DEFAULT NULL COMMENT '国际书号ISBN',
  `book_clcc` varchar(20) DEFAULT NULL COMMENT '中图分类号CLCC',
  `book_alcc` varchar(20) DEFAULT NULL COMMENT '美图分类号',
  `book_category1` varchar(50) DEFAULT NULL COMMENT '建议分类1',
  `book_category2` varchar(50) DEFAULT NULL COMMENT '建议分类2',
  `book_category3` varchar(50) DEFAULT NULL COMMENT '建议分类3',
  `book_series_cn` varchar(50) DEFAULT NULL COMMENT '中文丛书名',
  `book_series_english` varchar(50) DEFAULT NULL COMMENT '英文丛书名',
  `book_series_foreign` varchar(50) DEFAULT NULL COMMENT '外文丛书名',
  
  `book_series_fa` varchar(100) DEFAULT NULL COMMENT 'fa丛书名',
  `book_series_xi` varchar(100) DEFAULT NULL COMMENT 'xi丛书名',
  `book_series_e` varchar(100) DEFAULT NULL COMMENT 'e丛书名',
  
  `book_publish_count` int(0) DEFAULT '1' COMMENT '版次',
  `book_print_count` int(0) DEFAULT '1' COMMENT '印次',
  `book_copyright_word_count` float(10,0) DEFAULT NULL COMMENT '版权字数',
  `book_size` varchar(30) DEFAULT NULL COMMENT '用纸尺寸',
  `book_kaiben` varchar(30) DEFAULT NULL COMMENT '开本尺寸',
  `book_heigh` varchar(10) DEFAULT NULL COMMENT '书脊厚度',
  `book_language` varchar(20) DEFAULT NULL COMMENT '文种',
  `book_bilingual` varchar(200) DEFAULT NULL COMMENT '双语对应',
  `book_publish_time` varchar(15) DEFAULT NULL COMMENT '出版时间',
  `book_image_count` int(0) DEFAULT NULL COMMENT '插图数量',
  `book_image_spread` varchar(20) DEFAULT NULL COMMENT '插图分布',
  `book_table_count` int(0) DEFAULT NULL COMMENT '插表数量',
  `book_disk_type` varchar(8) DEFAULT NULL COMMENT '配盘介质',
  `book_disk_send` varchar(10) DEFAULT NULL COMMENT '光盘赠送方式',
  `book_paper_price` float(10,2) DEFAULT NULL COMMENT '纸书定价RMB',
  `book_paper_dollar_price` float(10,2) DEFAULT NULL COMMENT '纸数定价美元',
  `book_ebook_price` float(10,2) DEFAULT NULL COMMENT '电子书定价（人民币）',
  `book_ebook_dollar_price` float(20,2) DEFAULT NULL COMMENT '电子书美元定价',
  `book_cover_paper` varchar(50) DEFAULT NULL COMMENT '封面用纸',
  `book_cover_publish_color` varchar(30) DEFAULT NULL COMMENT '封面印刷颜色',
  `book_image_paper` varchar(50) DEFAULT NULL COMMENT '插页用纸',
  `book_image_publish_color` varchar(30) DEFAULT NULL COMMENT '插页印刷颜色',
  `book_neiwen_paper` varchar(50) DEFAULT NULL COMMENT '内文用纸',
  `book_neiwen_publish_color` varchar(30) DEFAULT NULL COMMENT '内文印刷颜色',
  `book_zhuangzhen_class` varchar(50) DEFAULT NULL COMMENT '装帧类型',
  `book_zhuangzhen_type` varchar(50) DEFAULT NULL COMMENT '装帧方式',
  `book_end_fumo` varchar(50) DEFAULT NULL COMMENT '后工艺覆膜',
  `book_end_uv` varchar(50) DEFAULT NULL COMMENT '后工艺UV',
  `book_end_lekou` varchar(50) DEFAULT NULL COMMENT '后工艺勒口',
  `book_end_sufeng` varchar(50) DEFAULT NULL COMMENT '后工艺塑封',
  `book_end_other` varchar(150) DEFAULT NULL COMMENT '后工艺其他',
  `book_design_cover_company` varchar(100) DEFAULT NULL COMMENT '封面设计公司',
  `book_design_style_company` varchar(100) DEFAULT NULL COMMENT '版式设计公司',
  `book_neiwen_page_count` int(0) DEFAULT '0' COMMENT '内文页数',
  `book_image_page_count` int(0) DEFAULT '0' COMMENT '插页页数',
  `book_publish_page_count` int(0) DEFAULT '0' COMMENT '总印张数',
  `book_weight` float(10,2) DEFAULT NULL COMMENT '重量（克/本）',
  `book_publish_explain` text COMMENT '印装说明',
  `book_news_publish_count` int(0) DEFAULT '0' COMMENT '新闻办印数',
  `book_news_distribute_count` int(0) DEFAULT '0' COMMENT '发行印数',
  `book_news_count` int(0) DEFAULT '0' COMMENT '总印数=新闻办印数+发行印数',
  `book_news_shangjiao_count` int(0) DEFAULT '0' COMMENT '上缴样书册数',
  `book_keyword_cn` text COMMENT '中文关键词',
  `book_keyword_english` text COMMENT '英文关键词',
  `book_keyword_foreign` text COMMENT '外文关键词',
  
  `book_keyword_fa` text COMMENT 'fa文关键词',
  `book_keyword_xi` text COMMENT 'xi关键词',
  `book_keyword_e` text COMMENT 'e关键词',
  
  `book_paper_neiwen_style_file` char(2) DEFAULT NULL COMMENT '纸质图书内文版式文件',
  `book_neiwen_serverpath` text,
  -- `book_neiwen_localpath` text,
  `book_paper_cover_style_file` char(2) DEFAULT NULL COMMENT '纸质图书封面版式文件',
  `book_cover_serverpath` text,
  -- `book_cover_localpath` text,
  `book_paper_font` char(2) DEFAULT NULL COMMENT '纸质图书内文使用字体',
  `book_font_serverpath` text,
  -- `book_font_localpath` text,
  `book_paper_publish_pdf` char(2) DEFAULT NULL COMMENT '纸质图书印刷PDF文件',
  `book_pdf_publish_serverpath` text,
  -- `book_pdf_publish_localpath` text,
  `book_paper_word` char(2) DEFAULT NULL COMMENT '纸质图书原word文件',
  `book_word_serverpath` text,
  -- `book_word_localpath` text,
  `book_e_xml` char(2) DEFAULT NULL COMMENT '数字出版XML文档',
  `book_xml_serverpath` text,
  -- `book_xml_localpath` text,
  `book_e_epub` char(2) DEFAULT NULL COMMENT '数字出版EPUB文件',
  `book_epub_serverpath` text,
  -- `book_epub_localpath` text,
  `book_e_mobi` char(2) DEFAULT NULL COMMENT '数字出版MOBI文件',
  `book_mobi_serverpath` text,
  -- `book_mobi_localpath` text,
  `book_e_pdf` char(2) DEFAULT NULL COMMENT '数字出版预览PDF文件',
  `book_pdf_read_serverpath` text,
  -- `book_pdf_read_localpath` text,
  `book_e_html` char(2) DEFAULT NULL COMMENT '数字出版HTML文件',
  `book_html_serverpath` text,
  -- `book_html_localpath` text,

  `book_contract` char(2) DEFAULT NULL COMMENT '合同',
  `book_contract_serverpath` text,

  `book_publish_soft` varchar(100) DEFAULT NULL COMMENT '排版使用的软件',
  `book_send_info` text COMMENT '其他送货地址、联系人及备注送货册书',
  `book_content_intr_cn` text COMMENT '内容简介（中文）',
  `book_content_intr_english` text COMMENT '内容简介（英文）',
  `book_content_intr_foreign` text COMMENT '内容简介（外文）',
  
  `book_content_intr_fa` text COMMENT '内容简介（fa文）',
  `book_content_intr_xi` text COMMENT '内容简介（x文）',
  `book_content_intr_e` text COMMENT '内容简介（e文）',

  `book_author_intr_cn` text COMMENT '作者简介（中文）',
  `book_author_intr_english` text COMMENT '作者简介（英文）',
  `book_author_intr_foreign` text COMMENT '作者简介（外文）',
  
  `book_author_intr_fa` text COMMENT '作者简介（fa文）',
  `book_author_intr_xi` text COMMENT '作者简介（x文）',
  `book_author_intr_e` text COMMENT '作者简介（e文）', 
  
  `book_editor_recommend_cn` text COMMENT '编辑推荐（中文）',
  `book_editor_recommend_english` text COMMENT '编辑推荐（英文）',
  `book_editor_recommend_foreign` text COMMENT '编辑推荐（外文）',
  
  
  `book_editor_recommend_fa` text COMMENT '编辑推荐（fa文）',
  `book_editor_recommend_xi` text COMMENT '编辑推荐（x文）',
  `book_editor_recommend_e` text COMMENT '编辑推荐（e文）',
  
  
  `book_cooperate_press` varchar(200) DEFAULT NULL COMMENT '合作出版社',
  `book_copyright_expires` varchar(15) DEFAULT NULL COMMENT '版权到期日期',
  `book_flag` tinyint(4) DEFAULT '0' COMMENT '版权到期是否处理标识，已经处理设置为1，没处理设置为0',
  `book_del_flag` tinyint(4) DEFAULT '0' COMMENT '标识图书是否删除，删除为1，没有删除为0',
  `book_type` tinyint(4) DEFAULT '1' COMMENT '用于标识电子书和纸质书，默认是纸质书和电子书，标识为‘3’，纸书标识为‘1’，电子书标识为’2‘',
  `user_id` int(0) NOT NULL COMMENT '创建图书的用户id',
  `book_evaluate_weight` float DEFAULT NULL,
  PRIMARY KEY (`book_id`),
  KEY `book_serial_number` (`book_serial_number`),
  KEY `book_isbn` (`book_isbn`),
  KEY `book_language` (`book_language`),
  KEY `book_name_cn` (`book_name_cn`,`book_publish_time`),
  KEY `book_name_english` (`book_name_english`),
  KEY `book_author` (`book_author`),
  KEY `book_editor` (`book_editor`),
  KEY `book_translator` (`book_translator`),
  KEY `book_category1` (`book_category1`)
) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8;


