-- ----------------------------
-- Table structure for wz_online_os
-- ----------------------------
DROP TABLE IF EXISTS `wz_online_os`;
CREATE TABLE `wz_online_os` (
  `os_id` int(11) NOT NULL AUTO_INCREMENT,
  `os_name` varchar(45) DEFAULT NULL,
  `os_desc` text,
  `os_flag` tinyint(1) unsigned zerofill NOT NULL DEFAULT '0',
  PRIMARY KEY (`os_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='存放电子书上线平台';


-- ----------------------------
-- Table structure for wz_book_format
-- ----------------------------
DROP TABLE IF EXISTS `wz_book_format`;
CREATE TABLE `wz_book_format` (
  `format_id` int(11) NOT NULL AUTO_INCREMENT,
  `book_id` int(10) unsigned NOT NULL COMMENT '图书id',
  `flag` tinyint(1) unsigned zerofill DEFAULT '1',
  PRIMARY KEY (`format_id`),
  UNIQUE KEY `book_id_UNIQUE` (`book_id`),
  KEY `flag` (`flag`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='存放已经转过的图书';


-- ----------------------------
-- Table structure for wz_book_online
-- ----------------------------
DROP TABLE IF EXISTS `wz_book_online`;
CREATE TABLE `wz_book_online` (
  `online_id` int(11) NOT NULL AUTO_INCREMENT,
  `book_id` int(11) NOT NULL COMMENT '图书的id',
  `os_id` int(11) NOT NULL COMMENT '对应平台的id',
  `is_online` tinyint(1) unsigned zerofill DEFAULT '0' COMMENT '没有上线 0 \n上线 1',
  `online_desc` text COMMENT '上线描述',
  PRIMARY KEY (`online_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='图书上线列表';